def binarySearch(arr, target):
    low = 0
    high = len(arr) - 1

    while low <= high:
        mid = (low + high) // 2

        if arr[mid] == target:
            return mid

        if arr[mid] < target:
            low = mid + 1
        else:
            high = mid - 1

    return -1

class Queue:
    def __init__(self):
        self.stk1 = []
        self.stk2 = []

    def enqueue(self, val):
        self.stk1.append(val)
        self.stk2.append(val)

    def dequeue(self):
        if self.stk1:
            return self.stk1.pop()
        return self.stk2.pop()

    def peek(self):
        if self.stk1:
            return self.stk1[-1]
        return self.stk2[-1]
